mapper.update:
UPDATE linkman
name = #{name,jdbcType = VARCHAR}, birth = #{birth,jdbcType = VARCHAR}, id_card = #{idCard,jdbcType = VARCHAR}, code = #{code,jdbcType = VARCHAR}, email = #{email,jdbcType = VARCHAR}, mobile = #{mobile,jdbcType = VARCHAR}, address = #{address,jdbcType = VARCHAR}, company = #{company,jdbcType = VARCHAR}, update_time = now(), ext1 = #{ext1,jdbcType = VARCHAR}, ext2 = #{ext2,jdbcType = VARCHAR}, ext3 = #{ext3,jdbcType = VARCHAR}
and id = #{id,jdbcType = VARCHAR} and uid = #{uid,jdbcType = VARCHAR}

mapper.delete:
DELETE FROM linkman

    and id = #{id,jdbcType = VARCHAR}
    and uid = #{uid,jdbcType = VARCHAR}

mapper.select:
SELECT

from linkman


        code = #{code,jdbcType=VARCHAR}


        id = #{id,jdbcType=VARCHAR}


limit 1
